import React from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'
import {Link} from 'react-router-dom'

import {
  deletePhoneFromBasket,
  jsonShowing,
  cleanBasket
} from 'actions'

import {
  getBasketPhonesWithCount,
  getTotalBasketPrice
} from 'selectors'

const Basket = ({
  basketCheckout,
  cleanBasket,
  phones,
  totalPrice,
  removePhoneFromBasket
}) => {
  const isBasketEmpty = R.isEmpty(phones)
  const viewBasket = () => (
      <div>
        {isBasketEmpty && <div className='h3'>Empty!</div>}
        <div className='table-responsive'>
          <table className='table-bordered table-striped table-condensed cf'>
            <tbody>
            {phones.map((phone, index) => (
              <tr key={index} className='item-checout'>
                <td className='first-column-checkout'>
                  <img
                    className='img-thumbnail'
                    src={phone.image}
                    alt={phone.name}
                  />
                </td>
                <Link to={`/phones/${phone.id}`}>
                  <h3 className='btn btn-link'>{phone.name}</h3></Link>
                <td>${phone.price}</td>
                <td>{phone.count}</td>
                <td>
                  <div scr></div>
                  <span className='delete-cart'
                    onClick={() => removePhoneFromBasket(phone.id)}>
                  </span>
                </td>
              </tr>
            ))}
            </tbody>
          </table>
        </div>
        {
          R.not(isBasketEmpty) &&
            <div className='row'>
            <div className='pull-right total-user-checkout'>
              <b className='h3'>Total:</b>
              <b className='h3'>${totalPrice}</b>
            </div>
          </div>
        }
      </div>
    )

  const viewSidebar = () => (
    <div className='buttons'>
      <Link
        className='btn btn-info'
        to='/'
      >
        <span className='glyphicon glyphicon-info-sign'></span>
        <span>Continue shopping!</span>
      </Link>
      {
        R.not(isBasketEmpty) &&
        <div className='buttons'>
          <button
            onClick={cleanBasket}
            className='btn btn-danger margins'
          >
            <span className='glyphicon glyphicon-trash'></span>
            <span>Clear cart</span>
          </button>
          <button
            className='btn btn-success margins'
            onClick={() => basketCheckout(phones)}
          >
            <span className='glyphicon glyphicon-envelope'></span>
            Checkout
          </button>
        </div>
      }
    </div>
  )

  return (
    <div className='view-container'>
      <div className='container'>
        <div className='row'>
          <div className='col-md-9'>
            {viewBasket()}
          </div>
          <div className='col-md-3'>
            {viewSidebar()}
          </div>
        </div>
      </div>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    phones: getBasketPhonesWithCount(state),
    totalPrice: getTotalBasketPrice(state)
  }
}

const mapDispatchToProps = {
  removePhoneFromBasket: deletePhoneFromBasket,
  cleanBasket,
  basketCheckout: jsonShowing
}

export default connect(mapStateToProps, mapDispatchToProps)(Basket)