import React, {Component} from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'
import {Link} from 'react-router-dom'

import {viewPhoneWithId, addPhoneToBasket} from 'actions'
import {getPhoneById} from 'selectors'
import BasketCart from 'components/basketCart'

class Phone extends Component {
  componentDidMount() {
    const phoneId = this.props.match.params.id
    this.props.viewPhoneWithId(phoneId)
  }

  viewFields() {
    const {phone} = this.props
    const columnField = R.compose(
      R.toPairs,
      R.pick([
        'cpu',
        'camera',
        'size',
        'weight',
        'display',
        'battery',
        'memory'
      ])
    )(phone)

    return columnField.map(([key, value]) => (
      <div className='column' key={key}>
        <div className='ab-details-title text-capitalize'>
          <p>{key}:</p>
        </div>
        <div className='ab-details-info'>
          {value}
        </div>
      </div>
    ))
  }

  viewSidebar() {
      const {phone, addPhoneToBasket} = this.props
      return (
          <div>
              <p className='lead'>Basket</p>
              <BasketCart/>
              <div className='form-group'>
                  <h1>{phone.name}</h1>
                  <h2>${phone.price}</h2>
              </div>
              <Link to='/' className='btn btn-info btn-block'>
                  Back to store
              </Link>
              <button
                  type='button'
                  className='btn btn-success btn-block'
                  onClick={() => addPhoneToBasket(phone.id)}
                >
                  Add to cart
              </button>
          </div>
      )
  }

  viewPhoneAndFields() {
    const {phone} = this.props
    return (
      <div className='thumbnail'>
        <div className='row'>
          <div className='col-md-6'>
            <img
              className='img-thumbnail img-class'
              src={phone.image}
              alt={phone.name}
            />
          </div>
          <div className='col-md-6'>
            {this.viewFields()}
          </div>
        </div>
        <div className='caption-full'>
          <h4 className='pull-right'>${phone.price}</h4>
          <h4 className='h3'>{phone.name}</h4>
          <p>{phone.description}</p>
        </div>
      </div>
    )
  }

  render() {
    return (
      <div className='view-container'>
        <div className='container'>
          <div className='row'>
            <div className='col-md-9'>
              {this.props.phone && this.viewPhoneAndFields()}
            </div>
            <div className='col-md-3'>
              {this.props.phone && this.viewSidebar()}
            </div>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    phone: getPhoneById(state, state.phonePage.id)
  }
}

const mapDispatchToProps = {
  viewPhoneWithId,
  addPhoneToBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Phone)