import React, {Component} from 'react'
import {connect} from 'react-redux'
import {Link} from 'react-router-dom'
import * as R from 'ramda'

import {
  viewPhones,
  addPhoneToBasket,
  viewCategories
} from 'actions'
import {getPhones} from 'selectors'
import Layout from 'containers/layout'

class Phones extends Component {
  componentDidMount() {
    this.props.fetchPhones()
    this.props.fetchCategories()
  }

  viewPhone(phone, index) {
    const {addPhoneToBasket} = this.props
    const shortDescription = `${R.take(70, phone.description)}...`

    return (
      <div className='col-sm-4 col-lg-4 book-list' key={index}>
        <div className='thumbnail'>
          <img
            className='img-thumbnail'
            src={phone.image}
            alt={phone.name}
          />
          <div className='caption'>
            <h4 className='pull-right'>${phone.price}</h4>
            <h4>
              <p className='h5'>{phone.name}</p>
            </h4>
            <p>{shortDescription}</p>
            <p className='itemButton'>
              <button
                className='btn btn-primary'
                onClick={() => addPhoneToBasket(phone.id)}
                aria-pressed='true'
              >
                Buy Now!
              </button>
              <Link
                to={`/phones/${phone.id}`}
                className='btn btn-default'
              >
                More info
              </Link>
            </p>
          </div>
        </div>
      </div>
    )
  }

  render() {
    const {phones} = this.props
    return (
      <Layout>
        <div className='books row'>
          {
            phones.map((phone, index) => this.viewPhone(phone, index))
          }
        </div>
      </Layout>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  phones: getPhones(state, ownProps)
})

const mapDispatchToProps = {
  fetchPhones: viewPhones,
  addPhoneToBasket,
  fetchCategories: viewCategories
}

export default connect(mapStateToProps, mapDispatchToProps)(Phones)
