import * as R from 'ramda'

import {
  VIEW_PHONES_SUCCESS,
  SEARCH_PHONE
} from 'actionTypes'

const initialState = {
  search: '',
  ids: []
}

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case VIEW_PHONES_SUCCESS:
      return R.merge(state, {
        ids: R.pluck('id', payload)
      })
    case SEARCH_PHONE:
      return R.merge(state, {
        search: payload
      })
    default:
      return state;
  }
}

